mapper.update:
update member3
set name = #{name} , age = #{age} ,gender = #{gender},email = #{email}
where id = #{id}

mapper.select:
       select * 
	   from (select rownum rnum , b.*
			 from (select *
			       from member3 
			       where id != 'admin'
					 	
			       order by id) b                                 
			 where rownum <= #{endrow} 
			) 
	   where rnum between #{startrow} and #{endrow}

sql.if:
and
id name age gender
like #{search_word}
